mapper.select:
SELECT b.id, a.*
FROM user_tbl a
INNER JOIN (
SELECT ROW_NUMBER() OVER(ORDER BY ${field} ${order}) AS id, ${field}

    , userId

FROM user_tbl

    where ${field} LIKE CONCAT('%', #{filter}, '%')


    where ${field} = ${filter}


) b ON a.userId = b.userId limit #{start},#{perPage};

SELECT DATE_FORMAT(createAt , '%Y-%m-%d') as x, count(*) as y
FROM bb.user_tbl
GROUP BY DATE_FORMAT(createAt , '%Y-%m-%d') order by DATE_FORMAT(createAt , '%Y-%m-%d');
